import React, {useState} from 'react';
import Username from '../LoginPage/Username';
import Password from '../LoginPage/Password';
import ButtonLogin  from '../LoginPage/ButtonLogin';
function LoginPage() {

    const [name, setName] = useState();
    const [pass, setPass] = useState("");
    const [nameError, setName1] = useState("");
    const [passError, setPass1] = useState("");

    

   
 
    const inputEvent = (event) => {
        // console.log(event.target.value)
        setName(event.target.value);
          };

          const inputPassEvent = (event) => {
            // console.log(event.target.value)
            setPass(event.target.value);
              };

            const valid = () => 
             {
                 if(!name.includes('@') )
                 {
                  setName1({  nameError: 'invalid name' })
                 }
               
             }

              const onSubmits = (event) => {
                // console.log('i clicked', event);
                event.preventDefault();
                if(valid()) {
                    alert("form submitted");
                }
               
            };
  
    return (
        <form onSubmit={onSubmits}>
        <div>
        <h1>Hello {name}</h1>
            <Username uname={name} change={inputEvent} />
                <p>{nameError}</p>
            <br/>
            <Password uname={pass} change={inputPassEvent}/>
                <p></p>
            <br/>
            <ButtonLogin click={onSubmits} />
        </div>
        </form>
    )
}

export default LoginPage
